        {props.parts[1]} {props.exercises[1]}
      </p>
      <p>
        {props.parts[2]} {props.exercises[2]}
      </p>
    </>
  )
}
*/

const Content = (props) => {
  return (
    <>
      <Part1 name = {props.parts[0]} number = {props.exercises[0]}/>
      <Part2 name = {props.parts[1]} number = {props.exercises[1]}/>
      <Part3 name = {props.parts[2]} number = {props.exercises[2]}/>
    </>
  )
}

const Part1 = (props) => {
  return (
    <>
    <p>
        {props.name} {props.number}
      </p>
    </>
  )
}

const Part2 = (props) => {
  return (
    <>
    <p>
        {props.name} {props.number}
      </p>
    </>
  )
}

const Part3 = (props) => {
  return (
    <>
    <p>
        {props.name} {props.number}
      </p>
    </>
  )
}

const Total = (props) => {
  return (
    <>
      <p>
        Number of exercises {props.total}
      </p>
    </>
  )
}

const App = () => {
  const course = 'Half Stack application development'
  const parts = ['Fundamentals of React', 'Using props to pass data', 'State of a component']
  const exercises = [10, 7, 14]
  const total = exercises.reduce((accumulator, currentValue) => accumulator + currentValue, 0)
  return (
    <>
      <Header course={course} />
      <Content parts = {parts} exercises = {exercises} />
      <Total total = {total} />
    </>
  )
}

export default App
